    <div class="input_area">
<div class=" highlight hl-ipython3"><pre><span></span><span class="n">file_names</span> <span class="o">=</span> <span class="kc">None</span>
<span class="k">for</span> <span class="p">(</span><span class="n">dirpath</span><span class="p">,</span> <span class="n">dirnames</span><span class="p">,</span> <span class="n">fnames</span><span class="p">)</span> <span class="ow">in</span> <span class="n">walk</span><span class="p">(</span><span class="n">path</span><span class="p">):</span>
    <span class="n">file_names</span> <span class="o">=</span> <span class="n">fnames</span>

<span class="n">single_image_file</span> <span class="o">=</span> <span class="p">[]</span>
<span class="n">multiple_image_file</span> <span class="o">=</span> <span class="p">[]</span>
<span class="n">genus_single</span> <span class="o">=</span> <span class="p">[]</span>
<span class="n">species_single</span> <span class="o">=</span> <span class="p">[]</span>
<span class="n">genus_multiple</span> <span class="o">=</span> <span class="p">[]</span>
<span class="n">species_multiple</span> <span class="o">=</span> <span class="p">[]</span>

<span class="k">for</span> <span class="n">f</span> <span class="ow">in</span> <span class="n">file_names</span><span class="p">:</span>
    <span class="n">forig</span> <span class="o">=</span> <span class="n">copy</span><span class="o">.</span><span class="n">copy</span><span class="p">(</span><span class="n">f</span><span class="p">)</span>
    <span class="k">if</span> <span class="s1">&#039; &#039;</span> <span class="ow">in</span> <span class="n">f</span><span class="p">:</span>
        <span class="n">f</span> <span class="o">=</span> <span class="n">f</span><span class="o">.</span><span class="n">split</span><span class="p">(</span><span class="s1">&#039; &#039;</span><span class="p">)</span>
    <span class="k">elif</span> <span class="s1">&#039;_&#039;</span> <span class="ow">in</span> <span class="n">f</span><span class="p">:</span>
        <span class="n">f</span> <span class="o">=</span> <span class="n">f</span><span class="o">.</span><span class="n">split</span><span class="p">(</span><span class="s1">&#039;_&#039;</span><span class="p">)</span>
    <span class="k">if</span> <span class="s1">&#039;tiny&#039;</span> <span class="ow">in</span> <span class="n">forig</span> <span class="ow">and</span> <span class="nb">len</span><span class="p">(</span><span class="n">f</span><span class="p">)</span> <span class="o">!=</span> <span class="mi">2</span><span class="p">:</span>
        <span class="c1">#print(&#039;single = &#039; + str(f))</span>
        <span class="n">single_image_file</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">forig</span><span class="p">)</span>
        <span class="n">genus_single</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">f</span><span class="p">[</span><span class="mi">0</span><span class="p">])</span>
        <span class="n">species_single</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">f</span><span class="p">[</span><span class="mi">1</span><span class="p">])</span>
    <span class="k">else</span><span class="p">:</span>
        <span class="c1">#print(&#039;multi = &#039; + str(f))</span>
        <span class="n">multiple_image_file</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">forig</span><span class="p">)</span>
        <span class="n">genus_multiple</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">f</span><span class="p">[</span><span class="mi">0</span><span class="p">])</span>
        <span class="n">fm</span> <span class="o">=</span> <span class="n">f</span><span class="p">[</span><span class="mi">1</span><span class="p">]</span><span class="o">.</span><span class="n">split</span><span class="p">(</span><span class="s1">&#039;.&#039;</span><span class="p">)[</span><span class="mi">0</span><span class="p">]</span>
        <span class="n">species_multiple</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">fm</span><span class="p">)</span>

<span class="k">for</span> <span class="n">i</span><span class="p">,</span> <span class="n">f</span> <span class="ow">in</span> <span class="nb">enumerate</span><span class="p">(</span><span class="n">single_image_file</span><span class="p">):</span>
    <span class="n">s</span> <span class="o">=</span> <span class="nb">str</span><span class="p">(</span><span class="n">genus_single</span><span class="p">[</span><span class="n">i</span><span class="p">])</span> <span class="o">+</span> <span class="s1">&#039;, &#039;</span> <span class="o">+</span> <span class="nb">str</span><span class="p">(</span><span class="n">f</span><span class="p">)</span>
    <span class="c1">#print(s)</span>

