        <span class="n">xr</span><span class="p">[</span><span class="mi">0</span><span class="p">:</span><span class="nb">int</span><span class="p">(</span><span class="n">center_crop_size</span><span class="p">[</span><span class="mi">0</span><span class="p">]),</span> <span class="mi">0</span><span class="p">:</span><span class="nb">int</span><span class="p">(</span><span class="n">center_crop_size</span><span class="p">[</span><span class="mi">1</span><span class="p">]),</span> <span class="n">c</span><span class="p">]</span> <span class="o">=</span> <span class="n">x</span><span class="p">[</span><span class="n">centerw</span> <span class="o">-</span> <span class="n">halfw</span><span class="p">:</span><span class="n">centerw</span> <span class="o">+</span> <span class="n">halfw</span><span class="p">,</span> <span class="n">centerh</span> <span class="o">-</span> <span class="n">halfh</span><span class="p">:</span><span class="n">centerh</span> <span class="o">+</span> <span class="n">halfh</span><span class="p">,</span> <span class="n">c</span><span class="p">]</span>

    <span class="k">return</span> <span class="n">xr</span>

<span class="k">def</span> <span class="nf">rotate</span><span class="p">(</span><span class="n">image_array</span><span class="p">:</span> <span class="n">ndarray</span><span class="p">,</span> <span class="n">degree</span><span class="p">):</span>
    <span class="k">return</span> <span class="n">sk</span><span class="o">.</span><span class="n">transform</span><span class="o">.</span><span class="n">rotate</span><span class="p">(</span><span class="n">image_array</span><span class="p">,</span> <span class="n">degree</span><span class="p">)</span>

<span class="k">def</span> <span class="nf">random_rotation</span><span class="p">(</span><span class="n">image_array</span><span class="p">:</span> <span class="n">ndarray</span><span class="p">):</span>
    <span class="c1"># pick a random degree of rotation between 25% on the left and 25% on the right</span>
    <span class="n">random_degree</span> <span class="o">=</span> <span class="n">random</span><span class="o">.</span><span class="n">uniform</span><span class="p">(</span><span class="o">-</span><span class="mi">25</span><span class="p">,</span> <span class="mi">25</span><span class="p">)</span>
    <span class="k">return</span> <span class="n">sk</span><span class="o">.</span><span class="n">transform</span><span class="o">.</span><span class="n">rotate</span><span class="p">(</span><span class="n">image_array</span><span class="p">,</span> <span class="n">random_degree</span><span class="p">)</span>

<span class="k">def</span> <span class="nf">random_noise</span><span class="p">(</span><span class="n">image_array</span><span class="p">:</span> <span class="n">ndarray</span><span class="p">):</span>
    <span class="c1"># add random noise to the image</span>
    <span class="k">return</span> <span class="n">sk</span><span class="o">.</span><span class="n">util</span><span class="o">.</span><span class="n">random_noise</span><span class="p">(</span><span class="n">image_array</span><span class="p">)</span>

<span class="k">def</span> <span class="nf">horizontal_flip</span><span class="p">(</span><span class="n">image_array</span><span class="p">:</span> <span class="n">ndarray</span><span class="p">):</span>
    <span class="c1"># horizontal flip doesn&#039;t need skimage, it&#039;s easy as flipping the image array of pixels !</span>
    <span class="k">return</span> <span class="n">image_array</span><span class="p">[:,</span> <span class="p">::</span><span class="o">-</span><span class="mi">1</span><span class="p">]</span>

<span class="k">def</span> <span class="nf">vertical_flip</span><span class="p">(</span><span class="n">image_array</span><span class="p">:</span> <span class="n">ndarray</span><span class="p">):</span>
    <span class="k">return</span> <span class="n">np</span><span class="o">.</span><span class="n">flipud</span><span class="p">(</span><span class="n">image_array</span><span class="p">)</span>
