<div class=" highlight hl-ipython3"><pre><span></span><span class="c1"># Image Data Augmentation</span>
<span class="kn">from</span> <span class="nn">copy</span> <span class="k">import</span> <span class="n">copy</span>


<span class="n">nrow</span> <span class="o">=</span> <span class="mi">3</span>
<span class="n">ncol</span> <span class="o">=</span> <span class="mi">13</span>

<span class="c1">#f, ax = plt.subplots(nrow, ncol, figsize=(20, 20))</span>
<span class="n">aug_images</span> <span class="o">=</span> <span class="p">[]</span>
<span class="n">aug_labels</span> <span class="o">=</span> <span class="p">[]</span>
<span class="k">for</span> <span class="n">j</span><span class="p">,</span> <span class="n">img</span> <span class="ow">in</span> <span class="nb">enumerate</span><span class="p">(</span><span class="n">train_imgs</span><span class="p">):</span>
    <span class="c1">#ax[j][0].imshow(img)</span>

    <span class="c1">#center crop</span>
    <span class="n">crop_size</span> <span class="o">=</span> <span class="p">(</span><span class="mi">3</span><span class="o">*</span><span class="n">img_width</span><span class="o">/</span><span class="mi">4</span><span class="p">,</span> <span class="mi">3</span> <span class="o">*</span> <span class="n">img_height</span><span class="o">/</span><span class="mi">4</span><span class="p">,</span> <span class="n">img_chan</span><span class="p">)</span>
    <span class="n">img_cc</span> <span class="o">=</span> <span class="n">copy</span><span class="p">(</span><span class="n">center_crop_rgb</span><span class="p">(</span><span class="n">img</span><span class="p">,</span> <span class="n">crop_size</span><span class="p">))</span>
    <span class="c1">#aug_images.append(img_cc)</span>
    <span class="c1">#aug_labels.append(train_lbls[j])</span>

    <span class="c1">#ax[j][1].imshow(img_cc)</span>

    <span class="c1"># horizontal flip</span>
    <span class="n">img_hf</span> <span class="o">=</span> <span class="n">horizontal_flip</span><span class="p">(</span><span class="n">img</span><span class="p">)</span>
    <span class="n">aug_images</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">img_hf</span><span class="p">)</span>
    <span class="n">aug_labels</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">train_lbls_interim</span><span class="p">[</span><span class="n">j</span><span class="p">])</span>
    <span class="c1">#ax[j][2].imshow(img_hf)</span>


    <span class="c1"># vertical flip</span>
    <span class="n">img_ud</span> <span class="o">=</span> <span class="n">vertical_flip</span><span class="p">(</span><span class="n">img</span><span class="p">)</span>
    <span class="n">aug_images</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">img_ud</span><span class="p">)</span>
    <span class="n">aug_labels</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">train_lbls_interim</span><span class="p">[</span><span class="n">j</span><span class="p">])</span>
    <span class="c1">#ax[j][3].imshow(img_ud)</span>

    <span class="c1"># random rotation</span>
    <span class="n">k</span><span class="o">=</span><span class="mi">0</span>
    <span class="k">for</span> <span class="n">angle</span> <span class="ow">in</span> <span class="nb">range</span><span class="p">(</span><span class="mi">15</span><span class="p">,</span> <span class="mi">360</span><span class="p">,</span> <span class="mi">45</span><span class="p">):</span> <span class="c1"># 8 different angles</span>
        <span class="n">img_rr</span><span class="o">=</span> <span class="n">copy</span><span class="p">(</span><span class="n">rotate</span><span class="p">(</span><span class="n">img</span><span class="p">,</span> <span class="n">angle</span><span class="p">))</span>
        <span class="n">aug_images</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">img_rr</span><span class="p">)</span>
        <span class="n">aug_labels</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">train_lbls_interim</span><span class="p">[</span><span class="n">j</span><span class="p">])</span>
        <span class="c1">#ax[j][4 + k].imshow(img_rr)</span>
        <span class="n">k</span> <span class="o">+=</span> <span class="mi">1</span>
    <span class="c1">#print(k)</span>

    <span class="c1"># random noise</span>
    <span class="n">img_rn</span><span class="o">=</span> <span class="n">random_noise</span><span class="p">(</span><span class="n">img</span><span class="p">)</span>
    <span class="n">aug_images</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">img_rn</span><span class="p">)</span>
    <span class="n">aug_labels</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">train_lbls_interim</span><span class="p">[</span><span class="n">j</span><span class="p">])</span>
    <span class="c1">#ax[j][4 + k].imshow(img_rn)</span>

<span class="n">aug_labels</span> <span class="o">=</span> <span class="n">np</span><span class="o">.</span><span class="n">array</span><span class="p">(</span><span class="n">to_categorical</span><span class="p">(</span><span class="n">aug_labels</span><span class="p">))</span>
<span class="c1">#plt.show()</span>
