<div class=" highlight hl-ipython3"><pre><span></span><span class="k">def</span> <span class="nf">categorical_to_cls</span><span class="p">(</span><span class="n">train_label</span><span class="p">):</span>
    <span class="k">for</span> <span class="n">ix</span><span class="p">,</span><span class="n">x</span> <span class="ow">in</span> <span class="nb">enumerate</span><span class="p">(</span><span class="n">train_label</span><span class="p">):</span>
        <span class="k">if</span> <span class="n">x</span> <span class="o">!=</span> <span class="mi">0</span><span class="p">:</span>
            <span class="k">break</span>

    <span class="k">return</span><span class="p">(</span><span class="n">ix</span><span class="p">)</span>
