<div class=" highlight hl-ipython3"><pre><span></span><span class="kn">from</span> <span class="nn">keras.callbacks</span> <span class="k">import</span> <span class="n">ReduceLROnPlateau</span>

<span class="sd">&#039;&#039;&#039;</span>
<span class="sd">def schedule_steps(epoch, steps):</span>
<span class="sd">    for step in steps:</span>
<span class="sd">        if step[1] &gt; epoch:</span>
<span class="sd">            print(&quot;Setting learning rate to {}&quot;.format(step[0]))</span>
<span class="sd">            return step[0]</span>
<span class="sd">        print(&quot;Setting learning rate to {}&quot;.format(steps[-1][0]))</span>
<span class="sd">        return steps[-1][0]</span>


<span class="sd">steps = [(1e-6, 2), (1e-5, 10), (5e-6, 20), (3e-6, 25)]</span>
<span class="sd">if steps is not None:</span>
<span class="sd">    lrSchedule = LearningRateScheduler(lambda epoch: schedule_steps(epoch, steps))</span>
<span class="sd">    callbacks.insert(0, lrSchedule)</span>

<span class="sd">callbacks.append(lrSchedule)</span>
<span class="sd">&#039;&#039;&#039;</span>

<span class="n">reduce_lr</span> <span class="o">=</span> <span class="n">ReduceLROnPlateau</span><span class="p">(</span><span class="n">monitor</span><span class="o">=</span><span class="s1">&#039;acc&#039;</span><span class="p">,</span> <span class="n">factor</span><span class="o">=</span><span class="mf">0.2</span><span class="p">,</span>
                              <span class="n">patience</span><span class="o">=</span><span class="mi">5</span><span class="p">,</span> <span class="n">min_lr</span><span class="o">=</span><span class="mf">0.00001</span><span class="p">)</span>
<span class="n">callbacks</span><span class="o">.</span><span class="n">append</span><span class="p">(</span><span class="n">reduce_lr</span><span class="p">)</span>
