    <span class="n">plt</span><span class="o">.</span><span class="n">subplot</span><span class="p">(</span><span class="mi">1</span><span class="p">,</span><span class="mi">2</span><span class="p">,</span> <span class="mi">2</span><span class="p">)</span>
    <span class="n">plt</span><span class="o">.</span><span class="n">imshow</span><span class="p">(</span><span class="n">test_images</span><span class="p">[</span><span class="n">pcl</span><span class="p">])</span>
    <span class="nb">print</span><span class="p">(</span><span class="s2">&quot;predicted class = &quot;</span> <span class="o">+</span> <span class="nb">str</span><span class="p">(</span><span class="n">pcl</span><span class="p">))</span>
    <span class="nb">print</span><span class="p">(</span><span class="s2">&quot;predicted class name = &quot;</span> <span class="o">+</span> <span class="nb">str</span><span class="p">(</span><span class="n">class_names</span><span class="p">[</span><span class="n">pcl</span><span class="p">]))</span>

    <span class="n">plt</span><span class="o">.</span><span class="n">show</span><span class="p">()</span>
</pre></div>

